<script>
import { disableBodyScroll, enableBodyScroll, clearAllBodyScrollLocks } from 'body-scroll-lock';
import gsap from 'gsap';
import { ScrollToPlugin } from "gsap/ScrollToPlugin";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollToPlugin, ScrollTrigger);

export default {
    head() {
        return {
            title: 'About Us',
            meta: [
                { charset: 'utf-8' },
                { name: 'viewport', content: 'width=device-width, initial-scale=1' },
                {
                    hid: 'description',
                    name: 'description',
                    content: 'Established in 2010, Idea Evolver is an audience marketing and custom software development company that specializes in medical device software\
                    (SaMD) and digital marketing for food, healthcare, and biotech companies.'
                },
                {
                    hid: 'description',
                    property: 'og:title',
                    content: 'About Us'
                },
                {
                    hid: 'description',
                    property: 'og:description',
                    content: 'Established in 2010, Idea Evolver is an audience marketing and custom software development company that specializes in medical device software\
                    (SaMD) and digital marketing for food, healthcare, and biotech companies.'
                },
                {
                    hid: 'description',
                    property: 'og:url',
                    content: 'https://idea-evolver.com/about-us'
                },
                {
                    property: 'og:image',
                    content: 'https://storage.googleapis.com/idea-evolver.com/images/open_graph.jpg'
                },
                {
                    property: 'og:type',
                    content: 'website'
                }
            ]
        }
    },
  layout: "scroller",
    mounted() {
    const targetElement = document.querySelector('body');
    clearAllBodyScrollLocks(targetElement);
    // SCROLL BUTTON
    let scrollArrow = document.getElementById("scrollArrow");
    let rootElement = document.documentElement;
    let scrollDistance = document.documentElement.clientHeight;

    function scrollToContent () {
        rootElement.scrollTo({
            top: scrollDistance,
            behavior: "smooth"
        })
    }

    scrollArrow.addEventListener("click", scrollToContent);

    gsap.utils.toArray('.anime').forEach(box => {
        gsap.fromTo(box, {
            autoAlpha: 0, 
            opacity: .7,
            scale: .2,
            }, {
            scrollTrigger: {
            trigger: box,
            once: true,
            },
            duration: 1.3,
            delay: .2,
            autoAlpha: 1, 
            scale: 1,
            opacity: 1,
            yoyo: true,
            ease: "expo",
        });
    });
  }
};
</script>

